onClick:
{{
  (async () => {
    // Basic validation
    if (!inpUsername.text.trim() || !inpPassword.text) {
      showAlert('Please enter both username and password', 'warning');
      return;
    }

    // Run the query
    await getUsers.run();

    // Read the rows from getUser.data
    const rows = getUsers.data;

    if (!rows || rows.length === 0) {
      showAlert('Invalid username or password', 'error');
      return;
    }

    const row = rows[0];
    const ok = row.passwordtbl === inpPassword.text;

    if (ok) {
      await storeValue('user', { username: row.usernametbl, loggedInAt: Date.now() });
      showAlert('Login successful!', 'success');
      navigateTo('Home'); // Change 'Home' if needed
    } else {
      showAlert('Invalid username or password', 'error');
    }
  })()
}}
