{{ 
  inputMonthlyPaytbl_Upload.tableData.reduce((sum, row) => {
    const basic = Number(row.basicsalary) || 0;
    const benefits = Number(row.otherbenefits) || 0;
    const gross = basic + benefits;
    const nssf = gross * 0.10;
    const taxable = gross - nssf;

    let paye = 0;
    if (taxable <= 270000) {
      paye = 0;
    } else if (taxable <= 520000) {
      paye = (taxable - 270000) * 0.08;
    } else if (taxable <= 760000) {
      paye = 20000 + (taxable - 520000) * 0.20;
    } else if (taxable <= 1000000) {
      paye = 68000 + (taxable - 760000) * 0.25;
    } else {
      paye = 128000 + (taxable - 1000000) * 0.30;
    }

    const totalDeductions =
      nssf +
      paye +
      (Number(row.salaryadvance) || 0) +
      (Number(row.loandeduction) || 0) +
      (Number(row.helsb) || 0) +
      (Number(row.advancerecovery) || 0) +
      (Number(row.otherdeductions) || 0) +
      (Number(row.socialdeduction) || 0);

    const netPay = gross - totalDeductions;
    return sum + netPay;
  }, 0).toLocaleString('en-US')
}}
